INSERT INTO `auction_email_template` (`id`, `email_from`, `email_to`, `email_subject`, `email_content`, `template_name`, `template_code`) VALUES
(1, '##FROM_EMAIL##', '##TO_MAIL##', 'Welcome to ##SITE_NAME##', '<p>Dear ##USERNAME##, Hi We wish to say a quick hello and thanks for registering at ##SITE_NAME##. If you did not request this account and feel this is an error, please contact us at ##CONTACT_MAIL## Thanks, ##SITE_NAME## <br /><a href="##SITE_URL##">##SITE_URL##</a></p>', 'Welcome Email', 'welcome_email'),
(2, '##FROM_EMAIL##', '##TO_MAIL##', 'Forgot password', 'Dear ##USERNAME##,         \n\nYour account has been created.Please visit the following password to activate your account...\n\n##NEW_PASSWORD##\n\nThanks,\n##SITE_NAME##\n<br><a href="##SITE_LINK##">##SITE_LINK##</a>', 'Forgot password', 'forgot_password'),
(3, '##FROM_EMAIL##', '##TO_MAIL##', 'Password changed', 'Hi ##USERNAME##,\r\n\r\nAdmin reset your password for your  ##SITE_NAME## account.\r\n\r\nYour new password: ##PASSWORD##\r\n\r\nThanks,\r\n##SITE_NAME##\r\n##SITE_LINK##', 'Admin Change Password', 'admin_change_password'),
(4, '##FROM_EMAIL##', '##TO_MAIL##', '##SITE_NAME## ##SUBJECT##', '##MESSAGE##\n\n\n<br><a href="##SITE_URL##">##SITE_URL##</a>\n##SITE_NAME##', 'Admin - Auto Contact Request Reply', 'admin_auto_contact_request_reply'),
(5, '##FROM_EMAIL##', '##TO_MAIL##', 'Welcome to ##SITE_NAME##', 'Hi, \n\nA new user named ##USERNAME## has joined in ##SITE_NAME## account. \n\nThanks,\n##SITE_NAME##\n<br><a href="##SITE_LINK##">##SITE_LINK##</a>', 'New user joined', 'new_user_joined'),
(6, '##FROM_EMAIL##', '##TO_MAIL##', '[##SITE_NAME##]  Contact us Message', 'Dear Admin,\r\n\r\nUsername :##USERNAME##\r\n\r\nEmail    :##EMAIL##\r\n\r\nMessage  :##MESSAGE##\r\n\r\nTelephone:##TELEPHONE##\r\n\r\nIP       :##IP##\r\n\r\nThank you,\r\n##USERNAME##\r\n', 'Contact Us', 'contact_us'),
(7, '##FROM_EMAIL##', '##TO_MAIL##', '[##SITE_NAME##] ##SUBJECT##', 'Dear ##FIRST_NAME## ##LAST_NAME##,\n\n   Thanks for contacting us. We''ll get back to you shortly.\n\n   Please do not reply to this automated response. If you have not contacted us and if you feel this is an error, please contact us through our site \n<br><a href="##CONTACT_URL##">##CONTACT_URL##</a>\n\n\n------ On ##POST_DATE## you wrote from ##IP## -----\n\n##MESSAGE##\n\nThanks,\n##SITE_NAME##\n<br><a href="##SITE_LINK##">##SITE_LINK##</a>\n', 'Contact Us Auto Reply', 'contact_us_reply'),
(8, '##FROM_EMAIL##', '##TO_MAIL##', 'Welcome to ##SITE_NAME##', 'Dear ##USERNAME##,\n\n##SITE_NAME## team added you as a user in ##SITE_NAME##.\n\nYour account details,\nUsername: ##USEDTOLOGIN##\nPassword : ##PASSWORD##\n\nThanks,\n##SITE_NAME##\n<br><a href="##SITE_LINK##">##SITE_LINK##</a>', 'Admin User Add', 'admin_user_add'),
(9, '##FROM_EMAIL##', '##TO_MAIL##', 'Welcome to ##SITE_NAME##', 'Dear ##USERNAME##,\n\nYour ##SITE_NAME## account has been removed.\n\nThanks,\n##SITE_NAME##\n<br><a href="##SITE_LINK##">##SITE_LINK##</a>', 'Admin User Delete', 'admin_user_delete'),
(10, '##FROM_EMAIL##', '##TO_MAIL##', 'Welcome to ##SITE_NAME##', 'Your ##SITE_NAME## account has been activated                                                                                                ##SITE_NAME##\n<br><a href="##SITE_LINK##">##SITE_LINK##</a>\n', 'Admin User Active', 'admin_user_active'),
(11, '##FROM_EMAIL##', '##TO_MAIL##', 'Your ##SITE_NAME## account has been deactivated', 'Dear ##USERNAME##,\n\nYour ##SITE_NAME## account has been deactivated.\n\nThanks,\n##SITE_NAME##\n<br><a href="##SITE_LINK##">##SITE_LINK##</a>', 'Admin User Deactivate', 'admin_user_deactivate'),
(12, '##FROM_EMAIL##', '##TO_MAIL##', 'Password changed', 'Dear ##USERNAME##,\n\nAdmin reset your password for your  ##SITE_NAME## account.\n\nYour new password: ##PASSWORD##\n\nThanks,\n##SITE_NAME##\n<br><a href="##SITE_LINK##">##SITE_LINK##</a>', 'Admin Change Password', 'admin_change_password2'),
(13, '##FROM_EMAIL##', '##TO_MAIL##', 'Password changed', 'Dear ##USERNAME##,\n\nyour reset password for your  ##SITE_NAME## account.\n\nYour new password: ##PASSWORD##\n\nThanks,\n##SITE_NAME##\n<br><a href="##SITE_LINK##">##SITE_LINK##</a>', 'User Change Password', 'user_change_password'),
(14, '##FROM_EMAIL##', '##TO_MAIL##', 'Re : ##SUBJECT##   ##SITE_NAME##  ', 'Dear  ##USERNAME##,\n\nCongratulations..!\n\nYou are winner for this products...\n\n##PRODUCTNAME##\n\n##PRODUCTCOST##\n\n##CURRENTPRICE##\n\n##SAVEAMOUNT##\n\n##MESSAGE##\n\n\n<br><a href="##SITE_URL##">##SITE_URL##</a>\n##SITE_NAME##', 'Admin - User Winners  Reply', 'admin_winner_reply'),
(15, '##FROM_EMAIL##', '##TO_MAIL##', '##SITE_NAME##  ##SUBJECT##', '##MESSAGE##\r\n\r\n##USERNAME##,\r\n\r\n\r\n\r\n\r\n\r\n\r\n', 'News Letter', 'news_letter'),
(16, '##FROM_EMAIL##', '##TO_MAIL##', 'Welcome to ##SITE_NAME##', 'Hi ##USERNAME##,\n\nYour account has been created. \nPlease visit the following URL to activate your account.\n##ACTIVATION_URL##\n\nThanks,\n##SITE_NAME##\n##SITE_LINK##', 'Activation Request', 'activation_request'),
(17, '##FROM_EMAIL##', '##TO_MAIL##', 'Facebook Password changed', 'Dear ##USERNAME##,\n\nSite Username is: ##USERNAME##\nSite password is: ##PASSWORD##\n\nYou can change your password at any time.\n\nThanks,\n##SITE_NAME##\n<br><a href="##SITE_LINK##">##SITE_LINK##</a>', 'User Change Facebook Password', 'user_change_facebook_password'),
(18, '##FROM_EMAIL##', '##TO_MAIL##', 'Twitter Signup', 'Hi ##USERNAME##,\n\nSite Username is: ##USERNAME##\nSite password is: ##PASSWORD##\n\nYou can change your password at any time.\n\nThanks,\n##SITE_NAME##\n##SITE_LINK##', 'User Change Twitter Password', 'user_change_twitter_password'),
